        {
            if (currency.currency == "DKP")
            {
                DKP = currency.value;
                Debug.Log($"Currency: {currency.currency}, Value: {currency.value}");
            }
            if (currency.currency == "XRP")
            {
                XRP = currency.value;
                Debug.Log($"Currency: {currency.currency}, Value: {currency.value}");
            }
        }
    }
    onCompleted(DKP, XRP); // Callback with the results
