        public (int, int) GetDate(string input){
    StringBuilder dayString = new StringBuilder();
        StringBuilder monthString = new StringBuilder();
        
        foreach (char c in input)
        {
            if (Char.IsDigit(c))
            {
                dayString.Append(c);
            }
            else
            {
                monthString.Append(c);
            }
        }

        int selectedDay = int.Parse(dayString.ToString());

        // Convert month string to month number
        int selectedMonth = 0;
        switch (monthString.ToString())
        {
            case "January":
                selectedMonth = 1;
                break;
            case "February":
                selectedMonth = 2;
                break;
            case "March":
                selectedMonth = 3;
                break;
            case "April":
                selectedMonth = 4;
                break;
            case "May":
                selectedMonth = 5;
                break;
            case "June":
                selectedMonth = 6;
                break;
            case "July":
                selectedMonth = 7;
                break;
            case "August":
                selectedMonth = 8;
                break;
            case "September":
                selectedMonth = 9;
                break;
            case "October":
                selectedMonth = 10;
                break;
            case "November":
                selectedMonth = 11;
                break;
            case "December":
                selectedMonth = 12;
                break;
            default:
                Console.WriteLine("Invalid month");
                return (selectedDay, selectedMonth);
        }
        return (selectedDay, selectedMonth);

}
