            }
            int PASSIVE_Arcana = 0;
                        int PASSIVE_Fortitude = 0;
                        for(int _char = 0; _char < sPlayer.GetInformationSheets().Count; _char++){
                            if(sPlayer.GetInformationSheets()[_char].CharacterID == sheet.CharacterID){
