        IEnumerator LoadSubScenes()
        {
            while (!AzureLoaded)
                yield return null;
            Debug.Log("Loading Scenes");
                GetAllScenes();
                
                // scene 0 = Container
                // scene 1 = OVM
                // scene 2 = TOWN
                // scene 3-x = MATCH

