            print($"GeneratePoints start");
            float spacing = 1f;
            List<Vector2> points = new List<Vector2>();
            Vector2 startPos = spawnPoint;
            startPos.y -= 1; // move 1 unit down on the y-axis
            for (int i = 0; i < numPoints; i++)
            {
                points.Add(startPos);
            print($"GeneratePoint at {startPos}");
                startPos.x += spacing;
            }
            return points;
        }
